		<script id="result-default-search-template" type="text/x-handlebars-template">
				
				{{#if sports}}
					<h3>Sports</h3>
					<div class = "sport-list">
					{{#each sports}}
						<div class = "sport">
							<a href = "{{url}}" class = "sport-link">
								{{title}}
							</a>
						</div>
					{{/each}}
					</div>
				{{/if}}

				{{#if others}}
					<h3>Others</h3>
					<div class = "other-list">
					{{#each others}}
						<div class = "other">
							<a href = "{{url}}" class = "other-link">
								{{title}}
							</a>
						</div>
					{{/each}}
					</div>
				{{/if}}
		</script>

		<script id="result-filtered-search-template" type="text/x-handlebars-template">
			<h3>Sports</h3>
			<div class = "filtered-results">
				{{#each this}}
					<div class = "filtered-sport">
						<a href = "{{url}}" class = "heading">
							<h3>{{title}}</h3>
							<h4>{{teaser_title}}</h4>
						</a>
						<ul class = "subpages">
							{{#each sub_pages}}
								<li><a href="{{url}}">{{title}}</a></li>
							{{/each}}
						</ul>
					</div>
				{{/each}}
			</div>
		</script>

		<script id="result-google-search-template" type="text/x-handlebars-template">
			<div class = "google-results">
				<h3>{{queries.request.0.title}}</h3>
				<p>Searching for "{{queries.request.0.searchTerms}}" in Google</p>
				<p>{{queries.request.0.totalResults}} Result(s) Found</p>
				<ol>
					{{#each items}}
						<li>
							<h4>
								<a href = "{{link}}" target = "_blank">
									{{title}}
								</a>
							</h4>
							<div>
								<cite>{{formattedUrl}}</cite>
								<span>{{snippet}}</span>
							</div>
						</li>
					{{/each}}
				</ol>

				<div class = "pagination">
					{{#if queries.previousPage}}
						<a href = "#" class = "btn btn-small google-next" data-startindex = "{{queries.previousPage.0.startIndex}}" data-query = "{{queries.previousPage.0.searchTerms}}">Previous Page</a>
					{{/if}}

					{{#if queries.nextPage}}
						<a href = "#" class = "btn btn-small google-next" data-startindex = "{{queries.nextPage.0.startIndex}}" data-query = "{{queries.nextPage.0.searchTerms}}">Next Page</a>
					{{/if}}
				</div>
			</div>
		</script>

